import React, { useState, useEffect } from 'react';
import { addProduct, updateProduct } from '../api';

const ProductForm = ({ existingProduct, onProductSaved, onCancel }) => {
  const [product, setProduct] = useState({
    name: '',
    description: '',
    price: '',
    quantity: ''
  });

  useEffect(() => {
    if (existingProduct) {
      setProduct(existingProduct);
    }
  }, [existingProduct]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProduct((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (existingProduct) {
        await updateProduct(product.id, product);
      } else {
        await addProduct(product);
      }
      onProductSaved(); // Callback to refresh the product list
    } catch (error) {
      console.error('Error saving product:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: '20px' }}>
      <h3>{existingProduct ? 'Edit Product' : 'Add Product'}</h3>
      <input
        type="text"
        name="name"
        placeholder="Product Name"
        value={product.name}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="description"
        placeholder="Description"
        value={product.description}
        onChange={handleChange}
        required
      />
      <input
        type="number"
        name="price"
        placeholder="Price"
        value={product.price}
        onChange={handleChange}
        required
      />
      <input
        type="number"
        name="quantity"
        placeholder="Quantity"
        value={product.quantity}
        onChange={handleChange}
        required
      />
      <button type="submit">{existingProduct ? 'Update' : 'Add'} Product</button>
      <button type="button" onClick={onCancel}>Cancel</button>
    </form>
  );
};

export default ProductForm;
