import React, { useEffect, useState } from 'react';
import { fetchProducts, deleteProduct, addProduct, updateProduct } from '../api'; // Import the API functions
import ProductForm from './ProductForm';
import { Button, Card } from 'react-bootstrap';

function ProductList() {
  const [products, setProducts] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);

  useEffect(() => {
    const loadProducts = async () => {
      try {
        const productsData = await fetchProducts(); // Fetch products from the API
        setProducts(productsData);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };
    loadProducts();
  }, []);

  const handleDelete = async (id) => {
    try {
      await deleteProduct(id); // Call deleteProduct API
      setProducts((prevProducts) => prevProducts.filter((product) => product.id !== id)); // Update local state
    } catch (error) {
      console.error('Error deleting product:', error);
    }
  };

  const handleEdit = (product) => {
    setSelectedProduct(product);
    setIsEditing(true);
  };

  const handleAdd = () => {
    setSelectedProduct(null);
    setIsEditing(true);
  };

  const handleProductSaved = async (product) => {
    if (selectedProduct) {
      // If we're editing, update the product
      await updateProduct(selectedProduct.id, product); // Call updateProduct API
      setProducts((prevProducts) =>
        prevProducts.map((p) => (p.id === selectedProduct.id ? { ...p, ...product } : p))
      );
    } else {
      // If we're adding, create a new product
      const newProduct = await addProduct(product); // Call addProduct API
      setProducts((prevProducts) => [...prevProducts, newProduct]); // Update local state
    }
    setIsEditing(false);
    setSelectedProduct(null);
  };

  const handleCancel = () => {
    setIsEditing(false);
    setSelectedProduct(null);
  };

  return (
    <div>
      <h2>Product List</h2>
      {isEditing ? (
        <ProductForm
          existingProduct={selectedProduct}
          onProductSaved={handleProductSaved}
          onCancel={handleCancel}
        />
      ) : (
        <Button variant="primary" onClick={handleAdd}>
          Add Product
        </Button>
      )}
      {products.length > 0 ? (
        <div className="product-list">
          {products.map((product) => (
            <Card key={product.id} style={{ margin: '10px' }}>
              <Card.Body>
                <Card.Title>{product.name}</Card.Title>
                <Card.Text>
                  <strong>Description:</strong> {product.description}
                  <br />
                  <strong>Price:</strong> ${product.price}
                  <br />
                  <strong>Quantity:</strong> {product.quantity}
                </Card.Text>
                <Button variant="info" onClick={() => handleEdit(product)}>
                  Edit
                </Button>
                <Button
                  variant="danger"
                  onClick={() => handleDelete(product.id)}
                  style={{ marginLeft: '10px' }}
                >
                  Delete
                </Button>
              </Card.Body>
            </Card>
          ))}
        </div>
      ) : (
        <p>No products available.</p>
      )}
    </div>
  );
}

export default ProductList;
